    private void pnMouseMoved(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_pnMouseMoved

        int x = evt.getX();
        int y = evt.getY();

        lblPosicao.setText(String.format("%03d,%03d", x, y));
        
    }//GEN-LAST:event_pnMouseMoved

    private void pnMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_pnMouseClicked
        
        //Coordenada do Clique do usuário
        int x = evt.getX();
        int y = evt.getY();
        
        //Adicionar a coordenada ao Polígono.
        p.addPoint(x, y);
        fig.ponto(x, y, Color.RED);
        
        //Quando o usuário clicar 2x
        if (evt.getClickCount() == 2){
            fig.poligono(p, Color.BLUE);
        }
        
    }//GEN-LAST:event_pnMouseClicked

    private void btnRetanguloActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnRetanguloActionPerformed
        int lagura = 400;
        int altura = 200;
        fig.retangulo(100, 100, lagura, altura, Color.red);
    }//GEN-LAST:event_btnRetanguloActionPerformed

    private void btnCirculoActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnCirculoActionPerformed
        int raio = 200;
        fig.circulo(200, 100, raio, Color.red);
    }//GEN-LAST:event_btnCirculoActionPerformed

    private void btnGradienteActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnGradienteActionPerformed

        Color ini = Color.RED;
        Color fim = Color.BLUE;
        
        //Coordenada inicial do Gradiente
        int xIni = 0;
        int yIni = 400;
        
        //Coordenada final do Gradiente
        int xFim = 600;
        int yFim = 0;
        
        fig.gradiente(ini, fim, xIni, yIni, xFim, yFim);
        
    }//GEN-LAST:event_btnGradienteActionPerformed

    private void btnTriangulo2ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnTriangulo2ActionPerformed
        fig.limpar();
    }//GEN-LAST:event_btnTriangulo2ActionPerformed

