    private void configurarFormulario() {
        this.setResizable(false);
        this.setTitle("Computação Gráfica");
        this.setLocationRelativeTo(null);

        fig = new Figura(pn.getPreferredSize());
        pn.add(fig);
        fig.ponto(fig.getWidth() / 2, fig.getHeight() / 2, Color.blue);
        p = new Polygon();
    }
